mapper.update:
update user
account=#{account}, name=#{name}, password=#{password}, age=#{age}, sex=#{sex}, phone=#{phone}, nick_name=#{nickName}, mail=#{mail}, update_time=#{updateTime}, update_user=#{updateUser},
where id = #{id, jdbcType = BIGINT}

mapper.select:
select id,account, password, name, age, sex, phone, nick_name, mail,create_time,
       update_time, create_user, update_user, deleted
from user where id = #{id}

select id,account, password, name, age, sex, phone, nick_name, mail,create_time,
       update_time, create_user, update_user, deleted
from user
where deleted = 0

    and id = #{id}


    and name = #{name}


    and account = #{account}
